<div class="layui-card layui-bg-gray"><?php if(!(empty($title) || (($title instanceof \think\Collection || $title instanceof \think\Paginator ) && $title->isEmpty()))): ?><div class="layui-card-header notselect"><span class="layui-icon layui-icon-next font-s10 color-desc margin-right-5"></span><?php echo htmlentities((isset($title) && ($title !== '')?$title:'')); ?><div class="pull-right"><button data-modal='<?php echo url("readerAll"); ?>' data-title="全部标为已读" class='layui-btn layui-btn-sm layui-btn-primary'>全部标为已读</button></div></div><?php endif; ?><div class="layui-card-body"><div class="think-box-shadow"><?php if(empty($list) || (($list instanceof \think\Collection || $list instanceof \think\Paginator ) && $list->isEmpty())): ?><blockquote class="layui-elem-quote">没 有 记 录 哦！</blockquote><?php else: ?><table class="layui-table" lay-skin="line"><thead><tr><th class='list-table-sort-td'><button type="button" data-reload class="layui-btn layui-btn-xs">刷 新</button></th><th></th><th>标题</th><th>时间</th><th colspan="2"></th></tr></thead><tbody><?php foreach($list as $key=>$vo): ?><tr data-dbclick><td class='list-table-sort-td'></td><td class='text-left nowrap'><div id="reader_<?php echo htmlentities($vo['message_box_id']); ?>" class="inline-block sub-span-blue"><span id="reader_span_<?php echo htmlentities($vo['message_box_id']); ?>" class="fa fa-envelope-o"></span></div></td><td class='text-left nowrap'><div id="title_<?php echo htmlentities($vo['message_box_id']); ?>" class="inline-block sub-span-blue"><span><?php echo (isset($vo['title']) && ($vo['title'] !== '')?$vo['title']:''); ?></span></div></td><td class='text-left nowrap'><div id="create_time_text_<?php echo htmlentities($vo['message_box_id']); ?>" class="inline-block sub-span-blue"><span><?php echo (isset($vo['create_time_text']) && ($vo['create_time_text'] !== '')?$vo['create_time_text']:''); ?></span></div></td><td class='text-center nowrap notselect'><a data-dbclick onclick="reader('<?php echo htmlentities($vo['message_box_id']); ?>')" class="layui-btn layui-btn-xs" data-title="详情" data-modal='<?php echo url("info"); ?>?message_box_id=<?php echo htmlentities($vo['message_box_id']); ?>'>详 情</a></td></tr><?php endforeach; ?></tbody></table><?php endif; ?></div><script>    function reader(message_box_id){
        $("#reader_" + message_box_id).removeClass("sub-span-blue");
        $("#title_" + message_box_id).removeClass("sub-span-blue");
        $("#create_time_text_" + message_box_id).removeClass("sub-span-blue");
        $("#reader_span_" + message_box_id).removeClass("fa fa-envelope-o");
        $("#reader_span_" + message_box_id).addClass("fa fa-envelope-open-o");
    }
</script></div></div>