            }
            form.render();
        }
    });

    function exportExcel(){
        var url = "<?php echo url('exportExcel'); ?>?patent_no=" + $("#patent_no_search").val() + "&patent_name=" + $("#patent_name_search").val()
            + "&apply_people=" + $("#apply_people_search").val() + "&apply_date=" + $("#apply_date_search").val()
            + "&project_level_id=" + $("#project_level_id_search").val();
        var a = document.createElement("a");
        a.setAttribute("href",url);
        a.click();
    }

    form.render();
